} Это называют overload pattern , который в общем случае выглядит как-то так: struct overloaded; template <typename Func0>
struct overloaded<Func0> : std::remove_reference_t<Func0> { overloaded(Func0&& func0) : Func0(std::forward<Func0>(func0)) {} using std::remove_reference_t<Func0>::operator();
}; template <typename Func0, typename... Funcs>
struct overloaded<Func0, Funcs...> : std::remove_reference_t<Func0>, overloaded<Funcs...> { overloaded(Func0&& func0, Funcs&&... funcs) : Func0(std::forward<Func0>(func0)), overloaded<Funcs...>(std::forward<Funcs>(funcs)...) {} using std::remove_reference_t<Func0>::operator(); using overloaded<Funcs...>::operator();
